import React from 'react';
import { NavLink } from 'react-router-dom';
import styled from "styled-components";
import hk from './hk.jpg';
import github from './github.svg';
import linkedin from './linkedin.svg';
import profile from './profile.jpg'


export const App = () => {
  return (
    <Background>
              <Container>

        <Navigation>
          <MenuLink href="#home">
            <h3>Home</h3>
          </MenuLink>
          <MenuLink href="#about">          
            <h3>About</h3>
          </MenuLink>        
          <MenuLink href="#services">
            <h3>Services</h3>
          </MenuLink>
          <MenuLink href="#portfolio"> 
            <h3>Portfolio</h3>
          </MenuLink>          
          <MenuLink href="#contact">
            <h3>Contact</h3>
          </MenuLink>
        </Navigation>

<FrontPage id="home">
<h1>JOSEPHINE LAM</h1>
<h3>Developer Freelancer</h3>   
<IconContainer>
<Icon class="social-link" href="https://www.linkedin.com/in/josephine-lam-180197198/" target="_blank">
              <img class=" icon" src={linkedin} alt="linkedin-logo"></img>
            </Icon>
<Icon class="social-link" href="https://github.com/prosperpine" target="_blank">
              <img class=" icon" src={github} alt="github-logo"></img>

              </Icon>
</IconContainer>
</FrontPage>

<About id="about">
<AboutCard><h1> <ProfilePic src={profile}></ProfilePic>Hi there, I'm Soliman Doe</h1>
<p>
I'm a freelance developer and will help your business grow a sleek-looking website! I specialize in small businesses with big dreams. </p>
</AboutCard>
</About>

<Services id="services">
<h1>This is what I can do for you</h1>
<ServiceSection>

<ServiceCard>
  <h2>Build your website</h2>
  <i class="far fa-grin-stars"></i>

</ServiceCard>
<ServiceCard>
  <h2>Set it up</h2>
  <i class="far fa-play-circle"></i>
  <p>Depending on your own experience, this can inlude setting up a domain and hosting and and/or training in how to update your own website and add content to always have a fresh front for your business.</p>
</ServiceCard>
<ServiceCard>
  <h2>Continue to care for it</h2>
  <i class="fas fa-hand-holding-heart"></i>
</ServiceCard>

</ServiceSection>

</Services>
<Portfolio id="portfolio">
<h1>This is what I've done</h1>
<PortfolioSection>

</PortfolioSection>
</Portfolio>
<Contact id="contact">
<h1>How to contact me</h1>
</Contact>
      </Container>
    </Background>
  )
}


