import React from 'react';
import styled from "styled-components";
import { Card } from "./Card"



export const Portfolio = () => {

  const cards = [
    {
      title: "Build website", 
      description: "I can build your website",
      icon: <i class="fas fa-laptop"></i>,
    }, 
    {
      title: "Set it up", 
      description: " I will help you set it up",
      icon: <i class="far fa-play-circle"></i>,
    }, 
    {
      title: "Update plan", 
      description: " I will help you keep it updated",
      icon: <i class="fas fa-hand-holding-heart"></i>,
    }
  ];

  return(

<Section id="portfolio">
<h1>This is what I've done</h1>



      {cards.map((card) => 
        <PortfolioSection>

  <Card value= {card.title}
  text ={card.description}
  icon ={card.icon}/>

</PortfolioSection>

  )}

  )
</Section>
  )
}

const Section = styled.div`
height: 100vh; 
background-color: rgba(0, 0, 0, 0.8);
flex-direction: column;
align-items: center;  
display: flex; 
padding-top: 100px; 
padding-bottom: 50px; 
`

const PortfolioSection =styled.div`
box-sizing: border-box;
display: flex;
justify-content: center; 
padding-bottom: 50px;

`
