import React, { useState } from 'react';
import styled from "styled-components";

export const Services = () => {
  const [build, setBuild] = useState('')

  const buildhandler = () => {
    setBuild(build); 
  }

if (!build) {
  return (
    <Section id="services">
      <h1>This is what I can do for you</h1>
<ServiceSection>

<ServiceCard onclick={setBuild} >
  <ServiceIcon ><i class="fas fa-laptop"></i>

</ServiceIcon>
<h2>Build your website</h2>


</ServiceCard>
<ServiceCard>
<ServiceIcon>  <i class="far fa-play-circle"></i>
</ServiceIcon> 
<h2>Set it up</h2>
 <p>Depending on your own experience, this can inlude setting up a domain and hosting and and/or training in how to update your own website and add content to always have a fresh front for your business.</p>

</ServiceCard>
<ServiceCard>
<ServiceIcon>  <i class="fas fa-hand-holding-heart"></i>
</ServiceIcon>
<h2>Continue to care for it</h2>
</ServiceCard>
</ServiceSection>
    </Section>
  ); } else { return (
    <h1>build the site</h1>
  )}
}

const Section = styled.div`
min-height: 100vh; 
box-sizing: border-box;
flex-direction: column;
justify-content: center; 
align-items: center;  
display: flex; 
padding-top: 100px; 
padding-bottom: 100px; 

`

const ServiceSection =styled.div`
box-sizing: border-box;
display: flex;
justify-content: center; 
width: 80%;
padding-top: 50px; 
padding-bottom: 50px;
`

const ServiceCard = styled.button`
background-color: rgba(0, 0, 0, 0.8);
display: flex;
flex-direction: column; 
align-items: center; 
box-sizing: border-box;
width: 30%;
margin: 20px; 
height: 300px; 
padding: 20px; 
padding-top: 50px; 
`
const ServiceIcon = styled.div`
font-size: 3rem; 
margin-bottom: 1rem; 
`
