import React from 'react';
import styled from "styled-components";
import { PortfolioCard } from "./PortfolioCard";
import poems from "./poems.png";
import music from "./music.png"
import movies from "./movies.png"
import covid from "./covid.png"
import todo from "./todo.png"



export const Portfolio = () => {

  const cards = [
    {
      image: <img src={poems}/>
      ,
    }, 
    {
      image: <img src={music}/>,

    }, 
    {
      image: <img src={movies}/>,
    },
    {
      image: <img src={covid}/>,
    }
  ];

  return(
<Main id="portfolio"> 
<h1>Some of the things I've done</h1>


<FeaturedProjects >
<ProjectCard>
<a href="https://github.com/prosperpine/project-todos" target="_blank">

<ToDoProject>
  <ProjectImageText>TO DO APP</ProjectImageText>
</ToDoProject>
<ProjectCardText><ProjectCardTextSpan>TO DO APP</ProjectCardTextSpan> Web app built in React using Redux.</ProjectCardText>
<Tags >
  <TagsText><TagsMark>HTML5</TagsMark></TagsText>
  <TagsText><TagsMark>CSS</TagsMark></TagsText>
  <TagsText><TagsMark>JavaScript</TagsMark></TagsText>
  <TagsText><TagsMark>React</TagsMark></TagsText>
  <TagsText><TagsMark>Redux</TagsMark></TagsText>
</Tags>
</a> 
        </ProjectCard>


      {/* {cards.map((card) => 
        <PortfolioSection>

  <PortfolioCard value= {card.title}
  text ={card.description}
  icon ={card.icon}
  image = {card.image} />

</PortfolioSection>

  )} */}


</FeaturedProjects>
</Main>
  )
}

const Main = styled.div`
background-color: rgba(0, 0, 0, 0.8);

text-align: center; 
padding-top: 100px; 
`

const Section = styled.div`
min-height: 100vh; 
box-sizing: border-box;
justify-content: center; 
align-items: center;  
display: flex; 
padding-bottom: 100px; 
`

const FeaturedProjects = styled.div`
display: flex;
flex-direction: column;
flex-wrap: wrap;
}`


const ProjectCard = styled.div`
width: 200px;
height:200px;
display: flex; 
flex-direction: column; 
justify-content: center; 
align-items: center; 
margin: 20px 40px;
border: 1px solid white; 
background-color: rgba(0, 0, 0, 0.8);
:hover {
  border: 2.4px solid white; 
}
`
const ProjectCardTextSpan = styled.span`
font-weight: bold;
`


const ProjectImageText = styled.p`
color: white;
font-family: 'Montserrat';
font-weight: bold;
font-size: 1.2rem;
text-align: center;
`

const ProjectCardText = styled.p`
text-align: left;
font-family: 'Roboto', sans-serif;
margin-bottom: 2px;`

const Tags = styled.div`
text-align: left;
display: flex;
flex-wrap: wrap;
`
const TagsText = styled.p`
font-size: 0.8rem;
line-height: 1em;
box-sizing: border-box;
padding: 5px 10px;
`

const TagsMark = styled.mark`
box-sizing: border-box;
padding: 5px 10px;
background-color: red;
margin-right: 10px;
`





const ToDoProject = styled.div`
background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)),
    url(${todo});
  padding: 3rem;
  background-position: center;
  background-repeat: no-repeat;
  background-size: cover;
  position: relative;
`


const PortfolioSection =styled.div`
box-sizing: border-box;
display: flex;
justify-content: center; 
padding-bottom: 50px;

`
