import React, { useState } from 'react';
import {AnimateSharedLayout, motion} from 'framer-motion'
import styled from "styled-components";
import { Card } from "./Card"


export const Services = () => {

  const cards = [
    {
      title: "Build your website", 
      description: "I will build your website from your desired design or work out a design with you. Whether you need a static informative website or a dynamic site that you can update yourself, I will make sure that you get a website that suits your needs and brand {'\n'} hello",
      icon: <i class="fas fa-laptop"></i>,
    }, 
    {
      title: "Set it up", 
      description: " I will help you set it up",
      icon: <i class="far fa-play-circle"></i>,
    }, 
    {
      title: "Update plan", 
      description: " I will help you keep it updated",
      icon: <i class="fas fa-hand-holding-heart"></i>,
    }
  ];

  return (
<Main id="services">
<h1>This is what I can do for you</h1>

    <Section >
      {cards.map((card) => 
<ServiceSection>

  <Card value= {card.title}
  text ={card.description}
  icon ={card.icon}/>

  </ServiceSection>
  )}
    </Section>
    </Main>

  )
}

const Main = styled.div`
text-align: center; 
padding-top: 100px; 
`


const Section = styled.div`
min-height: 100vh; 
box-sizing: border-box;
justify-content: center; 
align-items: center;  
display: flex; 
padding-bottom: 100px; 

`

const ServiceSection =styled.div`
box-sizing: border-box;
display: flex;
justify-content: center; 
padding-bottom: 50px;
`
