import React from 'react';
import styled from "styled-components";
import hk from './hk.jpg';
import github from './github.svg';
import linkedin from './linkedin.svg';
import profile from './profile.jpg';
import { Services } from './Services';
import { Portfolio } from './Portfolio'


export const App = () => {
  return (
    <Background>
              <Container>

        <Navigation>
          <MenuLink href="#home">
            <p>Home</p>
          </MenuLink>
          <MenuLink href="#about">          
            <p>About</p>
          </MenuLink>        
          <MenuLink href="#services">
            <p>Services</p>
          </MenuLink>
          <MenuLink href="#portfolio"> 
            <p>Portfolio</p>
          </MenuLink>          
          <MenuLink href="#contact">
            <p>Contact</p>
          </MenuLink>
        </Navigation>

<FrontPage id="home">
<Title>josephine lam</Title>
<Subtitle>DEVELOPER - FREELANCER</Subtitle>   
<IconContainer>
<Icon class="social-link" href="https://www.linkedin.com/in/josephine-lam-180197198/" target="_blank">
              <img class=" icon" src={linkedin} alt="linkedin-logo"></img>
            </Icon>
<Icon class="social-link" href="https://github.com/prosperpine" target="_blank">
              <img class=" icon" src={github} alt="github-logo"></img>

              </Icon>
</IconContainer>
</FrontPage>

<About id="about">
<AboutCard><h1> <ProfilePic src={profile}></ProfilePic>Hi there, I'm Soliman Doe</h1>
<p>
I'm a freelance developer and will help your business grow a sleek-looking website! I specialize in small businesses with big dreams. </p>
</AboutCard>
</About>

<Services />
<Portfolio />



<Contact id="contact">
<h1>How to contact me</h1>
</Contact>
      </Container>
    </Background>
  )
}


const Background = styled.main`
background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.4)), url(${hk});
background-position: center;
background-repeat: no-repeat;
background-size: cover;
background-attachment: fixed;
height: 100%; 
width: 100%; 
`

const Container = styled.div`
`
const Navigation = styled.div`
background-color: rgba(0, 0, 0, 0.4);
display: flex; 
justify-content: flex-end; 
position: fixed;
top: 0; 
width: 100%; 
`
const MenuLink = styled.a`
padding-right: 20px; 
font-family: 'Montserrat', sans-serif;
font-weight: 200; 
font-size: 1.4rem; 
`

const FrontPage = styled.div`
display: flex; 
flex-direction: column; 
justify-content: center; 
align-items: center; 
height: 100vh; 
font-family: 'Montserrat', sans-serif;

` 
const Title = styled.h1`
font-size: 4rem; 
`
const Subtitle = styled.h3`
margin-top: 20px; 
margin-bottom: 20px; 
font-size: 1.6rem; 
`

const IconContainer = styled.div`
display: flex; 
`

const Icon=styled.a`
border: 1px solid white;
padding: 16px; 
border-radius: 50%;  
margin: 16px; 
`


const About = styled.div`
height: 100vh; 
background-color: rgba(0, 0, 0, 0.8);
display: flex; 
justify-content: center; 
align-items: center; 
`

const AboutCard =styled.div`
width: 80%;
`


const ProfilePic = styled.img`
width: 20%; 
height: auto;
box-shadow: 0 4px 8px 0 rgba(255, 255, 255, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
border-radius: 10px; 
float: left; 
margin-right: 20px; 

`
const Contact = styled.div`
height: 100vh; 
flex-direction: column;
justify-content: center; 
align-items: center;  
display: flex; 



`
