import React, { useState } from 'react';
import styled from "styled-components";
import {AnimateSharedLayout, motion} from 'framer-motion'

export function Card({value}) {
const [open,setOpen]=useState(false)

const cards = [
  {
    title: "Build website", 
    description: "I can build your website",
  }, 
  {
    title: "Set it up", 
    description: " I will help you set it up",
  }
];

return (
  <div>
{cards.map((card, index) => (

  <AnimateSharedLayout>
{open?
  <ExpandedCard onClick={()=>setOpen(false)}
            className='expanded-card'
            layoutId="expandable-card"
           style={{background:value}}
           id={card.id}
           key={card.id}>
<ExpandedCardTitle
          className='expanded-card-h'
          layoutId="expandable-card-h">
                       <h1>{card.description}</h1>
 {value}</ExpandedCardTitle>
<ExpandedCardText>Lorem ipsum dolor sit amet consectetur adipisicing elit.
Voluptate aliquam molestiae ratione sint magnam sequi fugiat u
llam earum distinctio fuga iure, ad odit repudiandae modi est alias ipsum aperiam.
Culpa?</ExpandedCardText>
</ExpandedCard>

: 
<NormalCard onClick={()=>setOpen(true)}
            className="normal-card"
            layoutId="expandable-card"
            style={{background:value}}>
                           <h1>{card.title}</h1>

<NormalCardTitle layoutId="expandable-card-h">{value}Normal card</NormalCardTitle>
</NormalCard>
}

</AnimateSharedLayout>
))}
</div>
)
}
export default Card;

const NormalCard = styled(motion.div) `
width: 200px;
  height:200px;
  display: grid;
  place-items: end center ;
  margin: 10px;
  border: 1px solid white; 
  `
const NormalCardTitle =styled(motion.h1)`
font-size: 18px;
  color:var(--fg);
  padding: 5px;
  text-align: center;`

  const ExpandedCard = styled(motion.div)`
  position: absolute;
  /* top:0px;
  left:0px; */
  width: 100vw;
  height:100%;
  z-index: 1;
  display: grid;
  place-items: center;
  background-color: rgba(0, 0, 0, 0.8);`


  const ExpandedCardText = styled(motion.p)`
  width:300px;
  padding: 5px;
  place-self:  start center;`

  const ExpandedCardTitle = styled(motion.h2)`
  font-size: 27px;
  color:var(--fg);
  width: 300px;
  padding: 5px;
  place-self: end center;`
