import React, { useState } from 'react';
import styled from "styled-components";
import {AnimateSharedLayout, motion} from 'framer-motion'

export function Card({value, text}) {
const [open,setOpen]=useState(false)



return (

  <ServiceSection>
    <AnimateSharedLayout>
{open?


  <ExpandedCard onClick={()=>setOpen(false)}
            className='expanded-card'
            layoutId="expandable-card"
           style={{background:value}}
          >
<ExpandedCardTitle
          className='expanded-card-h'
          layoutId="expandable-card-h">
 {value}</ExpandedCardTitle>
<ExpandedCardText>{text}</ExpandedCardText>
</ExpandedCard>


:   

<NormalCard onClick={()=>setOpen(true)}
            className="normal-card"
            layoutId="expandable-card"
            style={{background:value}}

                         >

<NormalCardTitle layoutId="expandable-card-h">{value}</NormalCardTitle>
</NormalCard>

}

</AnimateSharedLayout>
  </ServiceSection>

)
}
export default Card;

const ServiceSection =styled.div`
box-sizing: border-box;
display: flex;
justify-content: center; 
width: 80%;
padding-top: 50px; 
padding-bottom: 50px;
`

const NormalCard = styled(motion.div) `
width: 200px;
  height:200px;
  display: grid;
  place-items: end center ;
  margin: 10px;
  border: 1px solid white; 
  `
const NormalCardTitle =styled(motion.h1)`
font-size: 18px;
  color:var(--fg);
  padding: 5px;
  text-align: center;`

  const ExpandedCard = styled(motion.div)`
  position: absolute;
  /* top:0px;
  left:0px; */
  width: 100vw;
  height:auto;
  z-index: 1;
  display: grid;
  place-items: center;
  background-color: rgba(0, 0, 0, 0.8);`


  const ExpandedCardText = styled(motion.p)`
  width:300px;
  padding: 5px;
  place-self:  start center;`

  const ExpandedCardTitle = styled(motion.h2)`
  font-size: 27px;
  color:var(--fg);
  width: 300px;
  padding: 5px;
  place-self: end center;`
