import React from 'react';
import styled from "styled-components";
import hk from './hk.jpg';
import github from './github.svg';
import linkedin from './linkedin.svg';
import profile from './profile.jpg';
import { Services } from './Services';
import { Portfolio } from './Portfolio'


export const App = () => {
  return (
    <Background>
              <Container>

        <Navigation>
          <MenuLink href="#home">
            <p>Home</p>
          </MenuLink>
          <MenuLink href="#about">          
            <p>About</p>
          </MenuLink>        
          <MenuLink href="#services">
            <p>Services</p>
          </MenuLink>
          <MenuLink href="#portfolio"> 
            <p>Portfolio</p>
          </MenuLink>          
          <MenuLink href="#contact">
            <p>Contact</p>
          </MenuLink>
        </Navigation>

<FrontPage id="home">
<Title>josephine lam</Title>
<Subtitle>DEVELOPER - FREELANCER</Subtitle>   
<IconContainer>
<Icon class="social-link" href="https://www.linkedin.com/in/josephine-lam-180197198/" target="_blank">
              <img class=" icon" src={linkedin} alt="linkedin-logo"></img>
            </Icon>
<Icon class="social-link" href="https://github.com/prosperpine" target="_blank">
              <img class=" icon" src={github} alt="github-logo"></img>

              </Icon>
</IconContainer>
</FrontPage>

<About id="about">
<AboutCard><h1> <ProfilePic src={profile}></ProfilePic>Hi, I’m Josephine
</h1>
<AboutText>
I’m a freelancing front end developer based in Sweden. My mission is to get you the fresh and sleek-looking website that you or your business deserves! 
I create websites and web applications with both <Span>design</Span> and <Span>accessability</Span> in mind to make sure your brand will be available and appealing to all of your customers.
Do you already have design and hosting ready and just need someone to build the site for you?  Or are you a website newbie and don’t know anything about code, hosting or domains – either way, I can help!</AboutText></AboutCard>
</About>

<Services />
<Portfolio />



<Contact id="contact">
<h1>How to contact me</h1>
<ContactIcon><i class="far fa-envelope"></i>
</ContactIcon>
<ContactIcon>
<i class="fas fa-phone-alt"></i>
</ContactIcon>



</Contact>
      </Container>
    </Background>
  )
}


const Background = styled.main`
background-image: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.4)), url(${hk});
